import React, { Component } from 'react';
import { connect } from 'react-redux';

import ReactFileReader from 'react-file-reader';
import { AtomicBlockUtils, convertFromRaw, EditorState } from "draft-js";
import Editor, { createEditorStateWithText  } from "draft-js-plugins-editor";
import createImagePlugin from "draft-js-image-plugin";
 
import 'draft-js-image-plugin/lib/plugin.css';
import 'draft-js/dist/Draft.css';
 

require('../../assets/styles/button.css');

require('../../assets/styles/scrollbar.css');
require('../../assets/styles/input-box.css');
 
const imagePlugin = createImagePlugin();
const plugins = [imagePlugin];

const initialState = {
  entityMap: {
    "0": {
      type: "image",
      mutability: "IMMUTABLE",
      data: {
        src:
          "https://www.draft-js-plugins.com/images/canada-landscape-small.jpg"
      }
    }
  },
  blocks: [
    {
      key: "9gm3s",
      text:
        "You can have images in your text field. This is a very rudimentary example, but you can enhance the image plugin with resizing, focus or alignment plugins.",
      type: "unstyled",
      depth: 0,
      inlineStyleRanges: [],
      entityRanges: [],
      data: {}
    },
    {
      key: "ov7r",
      text: "445345345345345",
      type: "atomic",
      depth: 0,
      inlineStyleRanges: [],
      entityRanges: [
        {
          offset: 0,
          length: 1,
          key: 0
        }
      ],
      data: {}
    }
  ]
};

const text = 'Click on the + button below and insert "/images/canada-landscape-small.jpg" to add the landscape image. Alternativly you can use any image url on the web.';

/**
 * 输入框
 */
class InputBox extends Component {
  constructor(props) {
    super(props);

    this.state = {
      editorState: EditorState.createWithContent(convertFromRaw(initialState))
    };

  }

  componentDidMount() {
    const base64 = 'data:image/png;base64,';
    const newEditorState = this.insertImage(this.state.editorState, base64);

    this.setState({ editorState: newEditorState });
  }

  onChange = editorState => {
    this.setState({
      editorState
    });
  };

  focus = () => {
    this.editor.focus();
  };


  insertImage = (editorState, base64) => {
    const contentState = editorState.getCurrentContent();
    const contentStateWithEntity = contentState.createEntity(
      'image',
      'IMMUTABLE',
      { src: base64 },
    );
    const entityKey = contentStateWithEntity.getLastCreatedEntityKey();
    const newEditorState = EditorState.set(
      editorState,
      { currentContent: contentStateWithEntity },
    );
    return AtomicBlockUtils.insertAtomicBlock(newEditorState, entityKey, ' ');
  };

 
 
  handleFiles(files) {
    console.log(files.base64)
  }

  state = {
    editorState: createEditorStateWithText(text),
  };

  onChange = (editorState) => {
    this.setState({
      editorState,
    });
  };

  focus = () => {
    this.editor.focus();
  };



  render() {
     
    return (
      <div>
        {/* <ReactFileReader fileTypes={[".jpg", ".png"]} base64={true} multipleFiles={true} handleFiles={this.handleFiles}>
          <button className='btn'>Upload</button>
        </ReactFileReader> */}


        <div className='scollContainer editor-container'>

          <div    >
          <Editor
            editorState={this.state.editorState}
            onChange={this.onChange}
            plugins={plugins}
            ref={element => {
              this.editor = element;
            }}
          />
          </div>

        </div>
        <button className='send-msg-btn'>{'发送'}</button>
        
      </div>
    )
  }

 
}


function mapStateToProps(state) {
  return state;
}



const page = connect(mapStateToProps, null)(InputBox);

/**
* InputBox
*/
export { page as InputBox };
